package arithmaticoperatorslearning;

public class Arithmaticoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * Arithmaticoperators
		 * +
		 * -
		 * *
		 * /
		 * %
		 */
		int a=50, b=6;
		int c;
		c=a+b;
		System.out.println("Value of Additional operator " + c);
		int d;
		d=a-b;
		System.out.println("Value of Subtration operator " + d);
		int e;
		e=a*b;
		System.out.println("Value of Multiplication operator " + e);
		int f;
		f=a/b;
		System.out.println("Value of Division operator " + f);
		int g;
		g=a%b;
		System.out.println("Value of Modulus operator " + g);
		System.out.println("Arithmatic operator program completed");
	}

}
